<script>

function recargarMedia(e) {
	var NUM_INTENTOS = 3;
	
	var count = parseInt(jQuery(e).attr("data-timer-count"));	
	console.log("Recargando " +jQuery(e).prop("tagName")+" timerId:"+jQuery(e).attr("data-timer")+" con count:"+count+", src:"+jQuery(e).attr("src"));
	jQuery(e).attr("src", jQuery(e).attr("src"));
	if (count < NUM_INTENTOS) {
		count++;
        jQuery(e).attr("data-timer-count", count);
	} else {  //Se cancela el timer
        clearInterval(jQuery(e).attr("data-timer"));
        console.log("Demasiados reintentos -> Se cancela el timer de " +jQuery(e).prop("tagName")+" con Id:"+jQuery(e).attr("data-timer")+", num de reintentos:"+count);
		//jQuery(e).removeAttr("data-timer");
		//jQuery(e).removeAttr("data-timer-count");
	}
}

//Se ha quitado la llamada inicial de recargarMedias, para que no choque con la del timer y se ha bajado el tiempo del timer
function checkeoMedias() {
	var SHAREPOINT_TIMER = 1000;
	
	jQuery("img[src*='sharepoint.com']").each(function() {
	if( (typeof this.naturalWidth != "undefined" && this.naturalWidth == 0) 
    ||  this.readyState == 'uninitialized'                                  ) {
      //console.log("*** Error en la carga de imagen: "+jQuery(this).attr("src"));
	  console.log("*** Error en la carga de " +jQuery(this).prop("tagName")+" timerId:"+jQuery(this).attr("data-timer")+" con count:"+jQuery(this).attr("data-timer-count")+", src:"+jQuery(this).attr("src"));
		

      //Se mete un temporizador por si no se realizara la carga de la imagen la primera vez, que se vuelva a lanzar la carga. 
      if (jQuery(this).attr("data-timer") == undefined) {
      	jQuery(this).attr("data-timer-count", 0);
        var timerId = setInterval(function(element) {recargarMedia(element);}, SHAREPOINT_TIMER, this);
        jQuery(this).attr("data-timer", timerId);
        console.log("TimerId imagen:"+jQuery(this).attr("data-timer")+", creado para src:"+jQuery(this).attr("src"));
      }
		
      //recargarMedia(this);
    } 
  });

  jQuery("video[src*='sharepoint.com']").each(function() {
    //if (this.readystate == 0) { //No sabiendo como localizar el fallo de la carga -> se fuerza siempre una vez
      //console.log("*** Error en la carga de vídeo: "+jQuery(this).attr("src"));
	  console.log("*** Error en la carga de " +jQuery(this).prop("tagName")+" timerId:"+jQuery(this).attr("data-timer")+" con count:"+jQuery(this).attr("data-timer-count")+", src:"+jQuery(this).attr("src"));
	  
      if (jQuery(this).attr("data-timer") == undefined) {
        var timerId = setInterval(function(element) {recargarMedia(element);}, SHAREPOINT_TIMER, this);
        jQuery(this).attr("data-timer", timerId);
        console.log("TimerId video:"+jQuery(this).attr("data-timer")+", creado para src:"+jQuery(this).attr("src"));
        jQuery(this).attr("data-timer-count", 0);
      }
	  
      //recargarMedia(this);
    //} 
  });

  jQuery("audio[src*='sharepoint.com']").each(function() {
    //if (this.readystate == 0) { //No sabiendo como localizar el fallo de la carga -> se fuerza siempre una vez
      //console.log("*** Error en la carga de audio: "+jQuery(this).attr("src"));
	  console.log("*** Error en la carga de " +jQuery(this).prop("tagName")+" timerId:"+jQuery(this).attr("data-timer")+" con count:"+jQuery(this).attr("data-timer-count")+", src:"+jQuery(this).attr("src"));
	  
      if (jQuery(this).attr("data-timer") == undefined) {
        var timerId = setInterval(function(element) {recargarMedia(element);}, SHAREPOINT_TIMER, this);
        jQuery(this).attr("data-timer", timerId);
        console.log("TimerId audio:"+jQuery(this).attr("data-timer")+", creado para src:"+jQuery(this).attr("src"));
        jQuery(this).attr("data-timer-count", 0);
      }
	  
      //recargarMedia(this);
    //} 
  });

}

function aules_notify_if_course_2019 () {
    var showannouncement = false;
    $(".breadcrumb  > li a[itemprop='url'] > span").each(function() {
        if ($(this).text().includes('_2019_') == true) {
            showannouncement = true;
        } else {
            // Hay profesores que han editado cursos de 2019 y los han puesto como 2020
            if ($(this).parent().hasClass('dimmed_text') && $(this).text().includes('_2019')) {
                showannouncement = true;
            }
            if ($(this).text().includes('19/20')) {
                showannouncement = true;
            }
        }
    })

    if (showannouncement) {
        //html para añadir cartel
        var txt = "Aquest curs es de l'any académic 2019/2020. No tens permissos d'edició";

        // M.filter_mathjaxloader._lang no podemos usarlo ya que no carga cuando se ejecuta el script, o no tenemos acceso a el.
        $('#navbarSupportedContent li.nav-item a.nav-link').each(function() {
            if ($(this).text().trim() == 'Área personal') {
                txt = 'Este curso es del año académico 2019/2020. No tienes permisos de edición';
            }
        });

        var html = '<div class="row aules_2019_advertisement"><div id="page-navbar" class="col-12"><h4 style="background-color: #c80f2e;color:white;padding: 9px;">' + txt + '</h4></div></div>'
        $(html).insertBefore('#page-content');
    }
}

